import React, { useState } from "react";
import "../styles/main.css";
import sound from "../assets/sound.png";
import send from "../assets/send.png";

const Chathistory=()=>{
  return (
    <div className="oldchat">

      
    </div>
  );
}
const Chats=()=>{
  return(
    <div className="right">
      <div className="head"><span>Chats</span></div>
      <Chathistory/>
      <div className="curr_chat">
        <input placeholder="type a message" type="text" />
        <button className="send">
        <img src={send} alt="404 error" />
        </button>
      </div>
    </div>

  );
}
const VideoSubtitle=()=>{
  return (
    <div className="left">
      <div className="videobig">
        <div className="videosmall"></div>
      </div>
      <div className="subtitle">
        <div className="sound"><img src={sound} alt="notfound" /></div><span className="text">Subtitles..</span>
      </div>
    </div>
  );
}

// Heading Component : 
const Heading=({name,time})=>{
  return(
    <div className="heading">
      <div className="title">
        <span>{name}</span>
      </div>
      <div className="time">
        <div className="circle"></div>
        <span>{time}</span>
      </div>
      <div className="leave">
        <button className="btn" id="leave">
          Leave
        </button>
      </div>
    </div>
  );
}

//Body component  of Main :
const Body=()=>{
  return (
    <div className="body">
      <VideoSubtitle/>
      <Chats/>
    </div>

  );
}
const Main = () => {
  const [projectName,setProjectName]=useState('myprojectName');
  const [duration,setDuration]=useState(0);
  return (
    <div id="bg">
      <Heading name={projectName} time={duration}/>
      <Body/>
    </div>
  );
};

export default Main;
